import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import Loader from 'components/common/Loader';
import { getEvents, addEvent } from 'redux/event/eventOperation';
import {loadingSelector } from 'redux/event/eventsSelectors';
import AddBoxIcon from '@mui/icons-material/AddBox';
import { nanoid } from 'nanoid';
import { toast } from 'react-toastify/dist/react-toastify';
import '../DateCalendar.scss';



const CalendarForm = ({ data }) => {


  const dispatch = useDispatch();
  const loading = useSelector(loadingSelector);

  const [eventName, setEventName] = useState("");
  const [newEvent, setNewEvent] = useState(null);


//ADAPTED_NAME
  const normalizeName = name => {
    console.log(name[0])
    const firstUpCaseLetter = name[0].toUpperCase();
    const anoterLetter = name.slice(1, name.lenght);
    return `${firstUpCaseLetter}${anoterLetter}`;
  }

//GET_EVENTS

  useEffect(() => {
    dispatch(getEvents())

  }, [dispatch])



//ADD_NEW_EVENT
  const confirmContact = (event) => setNewEvent(event)

  useEffect(() => {
    if (!newEvent) return;

    const addEventFunc = async () => {
      try {
        dispatch(addEvent(newEvent));
        setNewEvent(null);
        toast.success(`Well Done ðŸ’ª :))`, {
        icon: `âœ…`
    });
      } catch (error) {
        return error.message
      }
    }

    addEventFunc()

  }, [dispatch, newEvent])


//SUBMIT_FORM
  const onSubFormData = e => {
    e.preventDefault();

    const objEvent = {
      name:normalizeName(eventName),
      id: nanoid(),
      data,
      isActive: false,
    };

    confirmContact(objEvent);
    setEventName("");
  };

  return (
    <>
      {loading && <Loader loading={loading} />} 
      <button className="btn-add-event">


        <AddBoxIcon
                  //  onClick={() => onRemoveEvent(item)}
        sx={{
        color: "#0b856c",
        fontSize: "50px"
          }} />
        <strong>Add an event</strong>       
      </button>     
       <form onSubmit={onSubFormData}>
          <div className="input-group">
            <input
              className="input-main"
              name='event'
              onChange={e => setEventName(e.target.value)}
              type="text"
              placeholder="New Event..."
              value={eventName}
            >
            </input>
            <button
              className="addButton btn-main"
              type="submit"
            >
              +
            </button>
          </div>
        </form>

    </>

  )
}

CalendarForm.propTypes = {
  data: PropTypes.string,
}

export default CalendarForm
