import React from "react";
import { connect } from "react-redux";
import CalendarForm from "./CalendarForm/CalendarForm";
import CalendarList from "./CalendarList/CalendarList";
import { nanoid } from "nanoid";
import './DateCalendar.scss'
import CalendarDay from "./CalendarDay/CalendarDay";


class Day extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {

    let cls = this.props.selected ? " day-active" : "";
    if (this.props.hasEvents) cls += " has-events";
    if (this.props.hasMatches) cls += " has-matches";


    let day = this.props.days;
    return day > 0 ? (
      <div
        className={"day" + cls}
        onClick={(e) => this.props.setDay(this.props.days, e)}
      >
        {this.props.days}
      </div>
    ) : (
      <div className="day day-empty"></div>
    );
  }
}

