import { useContext, useEffect, useState } from 'react';
import Switch from '@mui/material/Switch';
import { useDispatch, useSelector } from 'react-redux';
import { AlarmContext } from "../../context/ContextAlarm";
import { alarmState } from 'redux/alarm/alarmsSelectors';
import { getAlarms, toggleAlarm } from 'redux/alarm/operation';
import AlarmModal from 'components/common/AlarmModal/AlarmModal';
import NotificationsOffSharpIcon from '@mui/icons-material/NotificationsOffSharp';
import Modal from 'components/common/Modal';






const AlarmList = () => {

  const alarms = useSelector(alarmState);
  const dispatch = useDispatch();
  const [activeAlarm, setActiveAlarm] = useState(null);

  const { setAlarmTime, pauseAlarm, hasAlarm, setHasAlarm } =
  useContext(AlarmContext);

  // const [alarmsArray, setAlarmsArray] = useState(alarms);


  // const [checked, setChecked] = useState(true);


  //FIRST_FETCH

  useEffect(() => {
    dispatch(getAlarms())
  }, [dispatch]);


  //TOGGLE
// console.log(alarms);
  const handleChange = (item) => setActiveAlarm({ ...item, isToggle: !item.isToggle });

  useEffect(() => {
    if (!activeAlarm) return;
    const handlerToggleAlarm = () => {
      dispatch(toggleAlarm(activeAlarm))
      setActiveAlarm(null)
    }

    handlerToggleAlarm()
  }, [activeAlarm, dispatch])


    const setAlarm = () => {
    if (hasAlarm) {
      pauseAlarm();
      setHasAlarm(false);
      return;
    }

    // if (
    //   !hour.includes("Hour") &&
    //   !minutes.includes("Minutes") &&
    //   !amPmOption.includes("Am-Pm")
    // ) {
    //   setHasAlarm(true);
    //   setAlarmTime(`${hour}:${minutes} ${amPmOption}`);
    //   // console.log()
    // }
  };

  // console.log(activeAlarm)
  return (
    <>
    {hasAlarm && <AlarmModal />}

    <ul style={{backgroundColor:"white"}}>
      {alarms && alarms.map((item) => {
        return <li
          key={item.id}
          style={{backgroundColor:item.color}}
        >
          <span>{item.time}</span>
          <Switch
            value={item.time}
            checked={item.isToggle}
            onClick={hasAlarm? setAlarm:()=>handleChange(item)}
            // onChange={()=>handleChange(item)}
            inputProps={{ 'aria-label': 'controlled' }}

          />
       <hr/>
      </li>}
      )}

    </ul>

    </>
  )
}

AlarmList.propTypes = {}

export default AlarmList
