import React from 'react'
import PropTypes from 'prop-types';
import '../DateCalendar.scss';


const CalendarForm = ({value, submit, update}) => {
  // console.log(`value->`, value)
  // console.log(`submit->`, submit)
  //  console.log(`update->`, update)

    const addEvent=(e)=> {
    if (e) e.preventDefault();
    let event = this.state.event.trim();
    if (!event) return;
    let events = this.state.events;
    let date = this.state.cursor;
    if (!events[date]) events[date] = [];
    events[date].push(event);
    this.setState({ event: "", events: events });
    this.saveEvents();
  }
  return (
         <form onSubmit={(e) => addEvent(e)}>
        <div className="input-group">
          <input
            className="input-main"
            onChange={(e) => update(e.target.value)}
            type="text"
            placeholder="New Event..."
            value={value}
          ></input>
          <button
            className="addButton btn-main"
            type="submit"
            // class="btn-main"
          >
            +
          </button>
        </div>
      </form>
  )
}

CalendarForm.propTypes = {
  value: PropTypes.string,
  submit: PropTypes.func,
  update: PropTypes.func,

}

export default CalendarForm
