import React, { useContext, useEffect } from 'react'
import PropTypes from 'prop-types'
import { ThemeContext, themes } from 'components/context/themeContext';


import '../DateCalendar.scss';
import Appear from 'components/Appear';
import { useTranslation } from 'react-i18next';

const CalendarDaysForm = ({
  prevYear,
  prevMonth,
  activeMonth,
  getYear,
  nextMonth,
  nextYear,
  dayNames,
  days,
  resetDate
}) => {



  const { theme } = useContext(ThemeContext);
  const { t } = useTranslation();
  const lang = useTranslation();

    const getMonthName = (activeMonth) =>{
    // const jan = this.state.lang === "en"?"January":"Січень"
    return [
      lang[1].language === "en"?"January":"Січень",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      lang[1].language === "en"?"September":"Вересень",
      "October",
      "November",
      "December"
    ][activeMonth];
  }

  // useEffect(() => {



  // }, [lang])



  const checkThemes = theme === themes.light ? { color: "black" } : { color: "white" }
  const checkThemesArrow = theme === themes.light ? { color: "black" } : { color: "gold" }
  const checkThemesBtnBack = theme === themes.light ? {backgroundColor: "#000000aa"} : {backgroundColor: "#0000004f"}
  return (
    <>

     <Appear time={750}>
      {/* Month selector */}
      <div
        className="month"
        style={checkThemes}
      >
            <span className="month-selector">
              <a
            className="prevYear"
            style={checkThemesArrow}
                href="#"
                onClick={prevYear}>
                «
              </a>
              <a
            className="prev"
            style={checkThemesArrow}
                href="#"
                onClick={prevMonth}>
                ᐊ
              </a>
              <span className="month-active">
                <span>
              {/* {this.getMonthName(this.state.month)} */}
              {getMonthName(activeMonth)}
                </span>
                {/* <span style={{ color: "teal" }}>{" " + this.state.year}</span>
          </span> */}
                <span style={theme === themes.light ? {color:"teal"} : {color:"gold"}}>{" " + getYear}</span>
        </span>

              <a
            className="next"
            style={checkThemesArrow}
                href="#"
            // onClick={this.nextMonth}>
            onClick={nextMonth}>
                ᐅ
              </a>
              <a
            className="nextYear"
            style={checkThemesArrow}
                href="#"
            // onClick={this.nextYear}>
            onClick={nextYear}>
                »
              </a>
            </span>
      </div>

      {/* Days grid */}

        <div
          className="weekdays"
          style={checkThemes}
        >{dayNames}</div>
          <div className="days">
          {days}
          <div className='btn-back-wrap'>
            <button
          className="reset"
          href="#"
          onClick={resetDate}
          style={checkThemesBtnBack}
        >
              {t("calendar.buttonBackToday")}
            </button>
          </div>

        </div>
        </Appear>
    </>
  )
}

CalendarDaysForm.propTypes = {}

export default CalendarDaysForm
