import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

export default function SimpleSlider() {
  const  settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return (

    <Slider {...settings}>
       <p>Current period:</p>
      <div>
        <h3>January</h3>
      </div>
      <div>
        <h3>February</h3>
      </div>
      <div>
        <h3>March</h3>
      </div>
      <div>
        <h3>April</h3>
      </div>
      <div>
        <h3>May</h3>
      </div>
      <div>
        <h3>June</h3>
      </div>
      </Slider>
  );
}
