import { createContext } from "react";


let time = new Date().getHours();

const themes = {
  light: "light",
  dark: "dark",
};
const themesColor = time <= 12 ? themes.light : themes.dark;

const ThemeContext = createContext({
  theme: themesColor,
  toggleTheme: () => {},
});

export { ThemeContext, themes };
