import { useCallback, useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import { eventsState, loadingSelector } from 'redux/event/eventsSelectors';
import { toggleEvent,editEvent,deleteEvent } from 'redux/event/eventOperation';
import { canvasStyles } from '../../../data/confetti';
import ReactCanvasConfetti from "react-canvas-confetti";
import { ToastContainer, toast } from 'react-toastify/dist/react-toastify';
import { Checkbox } from '@mui/material';
import BackspaceIcon from '@mui/icons-material/Backspace';
import PublishedWithChangesRoundedIcon from '@mui/icons-material/PublishedWithChangesRounded';
import VerifiedRoundedIcon from '@mui/icons-material/VerifiedRounded';
import BorderColorRoundedIcon from '@mui/icons-material/BorderColorRounded';
import "react-toastify/dist/ReactToastify.css";
import '../DateCalendar.scss';
import Modal from 'components/common/Modal';
import EditEventCard from 'components/common/EditEventCard/EditEventCard';

// import Confetti from 'components/common/Confetti/Confetti';


const CalendarList = ({ data }) => { 
  const dispatch = useDispatch();
  const events = useSelector(eventsState); 
  // const loading = useSelector(loadingSelector);

  const [activeEvent, setActiveEvent] = useState(null);
  const [editActiveEvent, setEditActiveEvent] = useState(null);
  const [sameEditName, setSameEditName] = useState(null);
  const [removeEvent, setRemoveEvent] = useState(null);

  const [isOpenModal, setIsOpenModal] = useState(false);



  //CONFETTI_ANIMATION
    const refAnimationInstance = useRef(null);

  const getInstance = useCallback((instance) => {
    refAnimationInstance.current = instance;
  }, []);

  const makeShot = useCallback((particleRatio, opts) => {
    refAnimationInstance.current &&
      refAnimationInstance.current({
        ...opts,
        origin: { y: 0.7 },
        particleCount: Math.floor(700 * particleRatio)
      });
  }, []);

  const fire = useCallback(() => {
    makeShot(0.9, {
      spread: 26,
      startVelocity: 55
    });

    makeShot(0.9, {
      spread: 60
    });

    makeShot(0.95, {
      spread: 100,
      decay: 0.91,
      scalar: 2.8
    });

    makeShot(0.9, {
      spread: 120,
      startVelocity: 50,
      decay: 3.92,
      scalar: 2.2
    });

    makeShot(0.9, {
      spread: 120,
      startVelocity: 45
    });
  }, [makeShot]);

///OPEN_MODAL


  // OPEN MODAL
  const openEditModal = () => setIsOpenModal(true);

  // CLOSE MODAL
  const closeEditModal = () => setIsOpenModal(false);
  //TOGGLE_EVENT

  const onClickDone = (currentEvent) => {

    setActiveEvent({ ...currentEvent, isActive: !currentEvent.isActive })
    if (currentEvent.isActive) return;
    fire();
    toast.success(`Well Done 👌 :))`, {
      icon: `✅`
    });
  };

  useEffect(() => {
    if (!activeEvent) return;
    dispatch(toggleEvent(activeEvent));
    setActiveEvent(null);

  }, [activeEvent, dispatch]);

// EDIT_EVENT
  const onEditEvent = (activeItem) => {
    setEditActiveEvent(activeItem)
    openEditModal()
  };

  const confirmEdit = (editedName) => {
    if (editedName === editActiveEvent.name) {
      setSameEditName(editedName);
      toast.warning(`IT'S the same TODO 👐:))`)
    // setEditEvent(null);
    return;
    }

    setEditActiveEvent({ ...editActiveEvent, name: editedName });
    closeEditModal()

  };
  console.log(editEvent)
  console.log(sameEditName);

  useEffect(() => {
    if (!editActiveEvent ) return;
    const saveEditEvent = () => {

    dispatch(editEvent(editActiveEvent))

    toast.success(
    `Завдання "${editActiveEvent.name}" успішно змінене !!`
    );

      setEditActiveEvent(null);
      setSameEditName(null);


    closeEditModal();
    };

    saveEditEvent();
  }, [dispatch, editActiveEvent]);

  //DELETE_EVENT

  const onRemoveEvent = (activeDeleteEvent) => setRemoveEvent(activeDeleteEvent);

  useEffect(() => {
    if (!removeEvent) return;
    dispatch(deleteEvent(removeEvent))


  }, [dispatch, removeEvent])


  return (
    <>
      <div className="events">
{isOpenModal && <Modal
          closeModal={closeEditModal}
        >
          <EditEventCard
            activeElement={editEvent}
            onSubmit={confirmEdit}
          />
        </Modal>}
      <ul>
        {events && events.map(item => {
          if (data !== item.data) {
            return;
          }
          else{
            return <li key={item.id}>
              <Checkbox
                checked={item.isActive}
                onClick={() => onClickDone(item)}
                color="success"
                icon={<PublishedWithChangesRoundedIcon />}
                checkedIcon={<VerifiedRoundedIcon />}
                inputProps={{ 'aria-label': 'controlled' }}
                sx={{ '& .MuiSvgIcon-root': { fontSize: 38 } }}
              />
              <ReactCanvasConfetti refConfetti={getInstance} style={canvasStyles} />
              <strong>{item.name}</strong>
              <BorderColorRoundedIcon
                onClick={() => onEditEvent(item)}
                sx={{
                color: "#008CBA",
                fontSize: "30px"
            }}/>
              <BackspaceIcon
                onClick={() => onRemoveEvent(item)}
                sx={{
                color: "#f44336",
                fontSize: "30px"
              }}/>
          </li>
          }
        })}
      </ul>

    </div>
    <ToastContainer theme="dark"/>
    </>

  )
}

CalendarList.propTypes = {
  data: PropTypes.string,
}

export default CalendarList
