import { useContext, useEffect, useState } from 'react';
import Switch from '@mui/material/Switch';
import { useDispatch, useSelector } from 'react-redux';
import { AlarmContext } from "../../context/ContextAlarm";
import { alarmState } from 'redux/alarm/alarmsSelectors';
import { deleteAlarm, getAlarms, toggleAlarm } from 'redux/alarm/operation';
import AlarmModal from 'components/common/AlarmModal/AlarmModal';
import DeleteForeverRoundedIcon from '@mui/icons-material/DeleteForeverRounded';
import { ToastContainer,toast } from 'react-toastify/dist/react-toastify';
import "react-toastify/dist/ReactToastify.css";
import s from './AlarmList.module.css'
import NotificationsOffSharpIcon from '@mui/icons-material/NotificationsOffSharp';
import Modal from 'components/common/Modal';






const AlarmList = () => {

  const alarms = useSelector(alarmState);
  const dispatch = useDispatch();
  const [activeAlarm, setActiveAlarm] = useState(null);
  const [deleteId, setDeleteId] = useState(null);


  const {
    pauseAlarm,
    hasAlarm,
    setHasAlarm } = useContext(AlarmContext);


  // const [alarmsArray, setAlarmsArray] = useState(alarms);


  // const [checked, setChecked] = useState(true);


  //FIRST_FETCH

  useEffect(() => {
    dispatch(getAlarms())
  }, [dispatch]);


  //TOGGLE
// console.log(alarms);
  const handleChange = (item) => setActiveAlarm({ ...item, isToggle: !item.isToggle });

  useEffect(() => {
    if (!activeAlarm) return;
    const handlerToggleAlarm = () => {
      dispatch(toggleAlarm(activeAlarm))
      setActiveAlarm(null)
    }

    handlerToggleAlarm()
  }, [activeAlarm, dispatch])


    const setAlarm = () => {
    if (hasAlarm) {
      pauseAlarm();
      setHasAlarm(false);
      return;
    }

      //DELETE_CONTACT 

  const onDeleteAlarm = (id) => setDeleteId(id);

  useEffect(() => {
    if (!deleteId ) return;
    console.log(deleteId);
    dispatch(deleteAlarm(deleteId));
    toast.success(`Контакт видалений зі списку контактів :))`, {
        icon: `❎`
      });
    setDeleteId(null);

  }, [deleteContactId, dispatch])

    // if (
    //   !hour.includes("Hour") &&
    //   !minutes.includes("Minutes") &&
    //   !amPmOption.includes("Am-Pm")
    // ) {
    //   setHasAlarm(true);
    //   setAlarmTime(`${hour}:${minutes} ${amPmOption}`);
    //   // console.log()
    // }
  };

  // console.log(activeAlarm)
  return (
    <>
    {hasAlarm && <AlarmModal />}

    <ul>
      {alarms && alarms.map((item) => {
        return <li
          className={s.item}
          key={item.id}
          style={{backgroundColor: item.color}}
        >
          <span>{item.time}</span>
          <Switch
            value={item.time}
            checked={item.isToggle}
            onClick={hasAlarm? setAlarm:()=>handleChange(item)}
            // onChange={()=>handleChange(item)}
            inputProps={{ 'aria-label': 'controlled' }}

          />
          <DeleteForeverRoundedIcon
            sx={{
              color: "#3b423a",
              fontSize: "30px"
            }}
          />
       <hr/>
      </li>}
      )}

    </ul>

    </>
  )
}

AlarmList.propTypes = {}

export default AlarmList
