import { getEvents, addEvent, deleteEvent } from 'redux/event/eventOperation';
import { eventsState, loadingSelector,errorSelector } from 'redux/event/eventsSelectors';
import Loader from 'components/common/Loader';



const CalendarForm = ({value, submit, update}) => {
  // console.log(`value->`, value)
  // console.log(`submit->`, submit)
  //  console.log(`update->`, update)

  const dispatch = useDispatch();
  const loading = useSelector(loadingSelector);
  const error = useSelector(errorSelector);



  const [eventName, setEventName] = useState("");
  const [newEvent, setNewEvent] = useState(null);



    // const addEvent=(e)=> {
    // if (e) e.preventDefault();
    //   let event = this.state.event.trim();
    //   console.log()
    // if (!event) return;
    // let events = this.state.events;
    // let date = this.state.cursor;
    // if (!events[date]) events[date] = [];
    // events[date].push(event);
    // this.setState({ event: "", events: events });
    // this.saveEvents();
    // }


//ADAPTED_NAME
   const normalizeName = name => name
      .split(' ')
      .map(word => {
        const firstUpCaseLetter = word.charAt(0).toUpperCase();
        const anoterLetter = word.substring(1);
        return `${firstUpCaseLetter}${anoterLetter}`;
      })
     .join(' ');

//GET_EVENTS

  useEffect(() => {
    dispatch(getEvents())
  }, [dispatch])

//ADD_NEW_EVENT
  const confirmContact = (event) => setNewEvent(event)

  useEffect(() => {
    if (!newEvent) return;

    dispatch(addEvent(newEvent))

    setNewEvent(null);


  }, [dispatch, newEvent])


//SUBMIT_FORM
  const onSubFormData = e => {
    e.preventDefault();

    const objEvent = {
      name:normalizeName(eventName),
      id:nanoid()
    };

    confirmContact(objEvent);
    setEventName("");
  };

  return (
    <>
      {loading &&<Loader loading={loading}/>}
               <form onSubmit={onSubFormData}>
        <div className="input-group">
          <input
          className="input-main"
          name='event'
          onChange={e => setEventName(e.target.value)}
            type="text"
            placeholder="New Event..."
            value={eventName}
            pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
            title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
            required
          ></input>
          <button
            className="addButton btn-main"
            type="submit"
            // class="btn-main"
          >
            +
          </button>
        </div>
      </form>}
    </>

  )
}

CalendarForm.propTypes = {
  value: PropTypes.string,
  submit: PropTypes.func,
  update: PropTypes.func,

}

export default CalendarForm
