const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
  };

const slider = true;

const HomePage = () => {

  const checkMonth = e => {
    console.log(e.target.innerHTML);
  }
  return (
    <div>

      <Balance />


      <div>
        <button className={s.btnBack}>
          <a className={s.text} href="/">  MainPage </a>
        </button>
      </div>

      {!slider ?
        <div 
        className={s.sliderContainer}>
          <p className={s.textSlider}>
            Current period:</p>
         <SimpleSlider/>
        </div> :
        <div className={s.containerReports}>
        <a
          className={s.textReports}
            href="./">

          Reports X
        </a>
      </div>}




    </div>
  )
};

export default HomePage;
