import React from 'react'
import PropTypes from 'prop-types'
import '../DateCalendar.scss';
import { useSelector } from 'react-redux';
import { eventsState } from 'redux/event/eventsSelectors';

const CalendarDay = ({
  selected,
  days,
  setDay,
  data}) => {

  const allEvents = useSelector(eventsState);
  const hasEvents = allEvents.some(item=>console.log(item.data === data))
  let cls = selected ? " day-active" : "";

    // if (this.props.hasEvents) cls += " has-events";
    // if (this.props.hasMatches) cls += " has-matches";

    // let day = days;
    return days > 0 ? (
      <div
        className={"day" + cls}
        onClick={(e) => setDay(days, e)}
      >
        {days}
      </div>
    ) : (
      <div className="day day-empty"></div>
    );

}

CalendarDay.propTypes = {}

export default CalendarDay
