import { createAsyncThunk } from '@reduxjs/toolkit';
import { getItems,toggleItem,editItem, saveItem, deleteItem } from "../../utils/eventsApi";

const API_ENDPOINT = "/events";

const getEvents = createAsyncThunk('events/getEventsStatus', () => getItems(API_ENDPOINT)
);

const addEvent = createAsyncThunk('event/addEventStatus', (newEvent) => saveItem(API_ENDPOINT, newEvent));

const toggleEvent = createAsyncThunk('event/toggleEventStatus', async (updateEvent) => {

  const data = await toggleItem(API_ENDPOINT, updateEvent)
  return data
}
);
const deleteEvent = createAsyncThunk('event/deleteEventStatus', (removeEvent) => {
  console.log(removeEvent)
  return deleteItem(API_ENDPOINT, removeEvent)
}
);
export { getEvents, addEvent, toggleEvent, deleteEvent }
