export default function SimpleSlider() {
  const  settings = {
    // dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    prevArrow: <SamplePrevArrow />,
    nextArrow: <SampleNextArrow />,

  };
  return (

    <Slider {...settings}>
      <div >
        <h3 className={s.nameContainer}>January 2022</h3>
      </div>
      <div>
        <h3>February</h3>
      </div>
      <div>
        <h3>March</h3>
      </div>
      <div>
        <h3>April</h3>
      </div>
      <div>
        <h3>May</h3>
      </div>
      <div>
        <h3>June</h3>
      </div>
      </Slider>
  );
}
